def policy_decide(objective):
    normalized = normalize(objective)
    metadata = attach_metadata(normalized)

    if is_clear(metadata):
        return execute(metadata)

    weighted = apply_weights(metadata)
    if has_priority(weighted):
        return execute(weighted)

    match = procedural_match(weighted)
    if match:
        return execute(match)

    log_ambiguity(objective, metadata)
    request_clarification(objective)
    return suspend_execution()
